script.script:
import hudson.FilePath
import java.util.regex.Pattern

if(manager.logContains(".*py did not execute correctly.*") || manager.logContains(".*Traceback .most recent call last.*"))
{
    manager.createSummary("warning.gif").appendText("Jenkins Integration Script Failure", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Jenkins Integration Script Failure")
}
if (manager.logContains(".*Failed to acquire lock on environment.*"))
{
    manager.createSummary("warning.gif").appendText("Failed to acquire lock on environment", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Failed to acquire lock on environment")
}
if (manager.logContains(".*Environment Creation Failed.*"))
{
    manager.createSummary("warning.gif").appendText("Environment Creation Failed", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Environment Creation Failed")
}
if (manager.logContains(".*FLEXlm Error.*"))
{
    manager.createSummary("warning.gif").appendText("FLEXlm Error", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "FLEXlm Error")
}
if (manager.logContains(".*INCR_BUILD_FAILED.*"))
{
    manager.createSummary("warning.gif").appendText("Build Error", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Build Error")
}
if (manager.logContains(".*NOT_LINKED.*"))
{
    manager.createSummary("warning.gif").appendText("Link Error", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Link Error")
}
if (manager.logContains(".*Preprocess Failed.*"))
{
    manager.createSummary("warning.gif").appendText("Preprocess Error", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Preprocess Error")
}
if (manager.logContains(".*Value Line Error - Command Ignored.*"))
{
    manager.createSummary("warning.gif").appendText("Test Case Import Error", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Test Case Import Error")
}

if(manager.logContains(".*Abnormal Termination on Environment.*")) 
{
    manager.createSummary("warning.gif").appendText("Abnormal Termination of at least one Environment", false, false, false, "red")
    manager.buildUnstable()
    manager.addBadge("warning.gif", "Abnormal Termination of at least one Environment")
}
FilePath fp_i = new FilePath(manager.build.getWorkspace(),'VcTools_manage_incremental_rebuild_report.html')
FilePath fp_f = new FilePath(manager.build.getWorkspace(),'VcTools_full_report.html')
if ( fp_f.exists())
{
    manager.build.description =  fp_f.readToString()}
else
{
    manager.createSummary("warning.gif").appendText("General Failure", false, false, false, "red")
    manager.buildUnstable()
    manager.build.description = "General Failure,  Full Report Not Present. Please see the console for more information"
    manager.addBadge("warning.gif", "General Error")
}

def buildLog = manager.build.getLog()
Pattern pattern = Pattern.compile('Coverage: Statement.*?[(](.*?)[%][)]')
int coverage = 0
buildLog.eachMatch pattern, {
   manager.listener.logger.println(it)
    coverage = Integer.valueOf(it[1])
   }

if ( coverage < 60) {
      manager.buildFailure()
       manager.addBadge("error.gif", "Coverage percentage dropped below 60%")
 }
