script.script:
import hudson.FilePath
import java.util.regex.Pattern



def buildLog = manager.build.getLog()
Pattern pattern = Pattern.compile('(?m)select count \\(\\*\\) from (.*?);\\s(\\d+)')

String table = ""
int count  = 0

def expected_count = [:]
expected_count["files"] = 1835
expected_count["commands"] = 7079


buildLog.eachMatch pattern, {
    table = it[1]
    count = Integer.valueOf(it[2])
    if (count != expected_count[table]){
      manager.listener.logger.println ("here")
    }
}
