import hudson.FilePath
import java.util.regex.Pattern

import hudson.model.*

// Get the out variable
def config = new HashMap()
def bindings = getBinding()
config.putAll(bindings.getVariables())

def out = config['out']

class OutputClass
{
    OutputClass(out)  // Have to pass the out variable to the class
    {
    out.println ("Inside class")
    }
}



def buildLog = manager.build.getLog()
Pattern pattern = Pattern.compile('/(?m)select count [(][*][)] from (.*?);(\\d+)')

String table = ""
int count  =0 

def expected_count = [:]
expected_count["filecount"] = [1835]
expected_count["commands"] = [7079]

buildLog.eachMatch pattern, {
   manager.listener.logger.println(it)
    table = it[0]
    count = Integer.valueOf(it[1])
    out.println(table)
    out.println(count)
    if (count != expected_count[table])
      out.println ("here")


   }
